import React, {useState} from 'react';
import './App.css';
import arrowUp from './assets/img/arrow-up.png';
import osmar from './assets/img/logo-provvisorio.png'

class App extends React.Component {
  constructor(props) {
    super(props)
    this.chiSiamo = React.createRef(); 
    this.lavorazioni = React.createRef(); 
    this.contatti = React.createRef();
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img className='osmar-logo' src={osmar} alt="osmar-logo"/>
          <div className="link-servizi">
            <div className="link-chi-siamo" onClick={() => scrollToSection(this.chiSiamo)}>Chi Siamo</div>
            <div className="link-lavorazioni" onClick={() => scrollToSection(this.lavorazioni)}>Lavorazioni</div>
            <div className="link-contatti" onClick={() => scrollToSection(this.contatti)}>Contatti</div>
            <div className="language-selector">IT</div>
          </div>
        </header>
        <div className="App-body">
          <NavigationMenu chiSiamo={this.chiSiamo} lavorazioni={this.lavorazioni} contatti={this.contatti}/>
          <ScrollTopButton/>
          <div className="chi-siamo" ref={this.chiSiamo}></div>
          <div className="lavorazioni" ref={this.lavorazioni}></div>
          <div className="contatti" ref={this.contatti}></div>
        </div>
      </div>
    );
  }
}

/**
 * Button component used to scroll on window top, the button is displayed only when the page is scrolled of at least 400 units
 */
function ScrollTopButton() {
  const [showScroll, setShowScroll] = useState(false);

  const checkScrollTop = () => {
    if (!showScroll && window.pageYOffset > 300){
      setShowScroll(true)
    } else if (showScroll && window.pageYOffset <= 300){
      setShowScroll(false)
    }
  };

  window.addEventListener('scroll', checkScrollTop);

  return (
    <div 
      className={`scroll-top-button`} 
      onClick={() => scrollToTop()}
      style={{visibility: showScroll ? 'visible' : 'hidden'}}>
        <img className="arrow-icon" src={arrowUp} alt="arrow-up"/>
      </div>
  );
}

function NavigationMenu(props) {
  const [showScroll, setShowScroll] = useState(false);

  const checkScrollTop = () => {
    if (!showScroll && window.pageYOffset > 300) {
      setShowScroll(true)
    } else if (showScroll && window.pageYOffset <= 300) {
      setShowScroll(false)
    }
  };

  window.addEventListener('scroll', checkScrollTop);

  return (
    <div className="navigation-menu">
      <img className='osmar-logo' src={osmar} alt="osmar-logo" />
      <div className="link-servizi">
        <div className="link-chi-siamo" onClick={() => scrollToSection(props.chiSiamo)}>Chi Siamo</div>
        <div className="link-lavorazioni" onClick={() => scrollToSection(props.lavorazioni)}>Lavorazioni</div>
        <div className="link-contatti" onClick={() => scrollToSection(props.contatti)}>Contatti</div>
        <div className="language-selector">IT</div>
      </div>
    </div>
  );
}

/**
 * Function that scrolls the page to a specific div by its ref applying a smooth animation
 * @param {*} ref - reference of the element that has to be displayed
 */
function scrollToSection(ref) {
  ref.current.scrollIntoView({behavior: 'smooth'});
}

/**
 * Method used by scroll to top button to move the entire content to the top of the website
 */
function scrollToTop() {
  window.scrollTo({top: 0, behavior: 'smooth'});
}

export default App;
